// 导出为 html 文件
export const exportHtmlFile = (html: string, Title: string) => {
  // 生成 HTML 文件内容
  const result = exportHtmlTemplate(html, Title)
  // // 创建一个 Blob 对象
  const blob = new Blob([result], { type: "text/html" })
  // 创建一个下载链接
  const url = URL.createObjectURL(blob)
  const a = document.createElement("a")
  a.href = url
  a.download = `${Title}.html` // 设置下载文件的名称
  document.body.appendChild(a)
  // 触发下载
  a.click()
  // 清理 URL 对象
  URL.revokeObjectURL(url)
  document.body.removeChild(a)
}

